import {useContext, useState} from 'react'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import Container from 'react-bootstrap/Container'
import ProyectoContext from '../../context/Proyectos/proyectoContext'
import TareaContext from '../../context/tareas/tareaContext'
import './FormTareas.css'

const FormTareas = () => {
    const proyectosContext = useContext(ProyectoContext)
    const {proyecto} = proyectosContext
    const tareasContext = useContext(TareaContext)
    const {agregarTareas} = tareasContext
    const [tarea, guardarTarea] = useState({
        nombre : ''
    })
    const {nombre} = tarea
    if(!proyecto) return null
    const [proyectoSeleccionado] = proyecto
    const handleChange = e =>{
        guardarTarea({
            ...tarea,
            [e.target.name] : e.target.value
        })
    }
    const onSubmit = e => {
        e.preventDefault()
        tarea.proyectoId = proyectoSeleccionado.id
        agregarTareas()
    }
    return (
        <Container className='containerFormTareas d-flex justify-content-center align-items-center'>
            <Form 
                className='w-50'
                onSubmit={onSubmit}
                >
                <Form.Group>
                    <Form.Control 
                        type="text" 
                        placeholder="Ingresa un nombre a tu tarea"
                        name='nombre'
                        value={nombre}
                        onChange={handleChange}
                        />
                </Form.Group>

                <Button variant="none" type="submit" className='btnAgregarTarea' block>
                    AGREGAR TAREA
                </Button>
            </Form>
        </Container>
    );
}
 
export default FormTareas;
