import Container from 'react-bootstrap/Container'
import ListGroup from 'react-bootstrap/ListGroup'
import Tarea from './Tarea'
import Row from 'react-bootstrap/Row'
import './ListadoTareas.css'

const ListadoTareas = () => {
    const tareasProyecto = [
        {nombre:'Elegir Colores' , estado:true},
        {nombre:'Elegir BD' , estado:false},
        {nombre:'Elegir Plataforma' , estado:false},
        {nombre:'Elegir Componentes' , estado:true}
    ]
    return (
        <Container className='containerListadoTareas'>
            <Row className='d-flex justify-content-center m-0 p-0'><h2>PROYECTO</h2></Row>
            <Row className='d-flex justify-content-center m-0 p-0'>
                <ListGroup>
                    {tareasProyecto.length === 0
                        ? <h3>No hay tareas para mostrar !</h3>
                        : tareasProyecto.map(tarea => (
                            <Tarea tarea={tarea}></Tarea>
                        ))
                    }
                    
                </ListGroup>
            </Row>
        </Container>
    );
}
 
export default ListadoTareas;
