import Button from 'react-bootstrap/Button'
import './Tarea.css'

const Tarea = ({tarea}) => {
    return (
        <div className='containerTarea d-flex justify-content-center mt-3'>
            <div className='w-50 tarea d-flex align-items-center'>
                <p className='m-0'>{tarea.nombre}, {tarea.estado ? 'Verdarero' : 'Falso'}</p>
                <div className='d-flex justify-content-end'>
                    <Button variant="primary" className="btnTarea">EDITAR</Button>
                    <Button variant="danger" className="btnTarea">ELIMINAR</Button>
                </div>
            </div>
        </div>
    );
}
 
export default Tarea;
