import {useState} from 'react'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import Container from 'react-bootstrap/Container'
import './NuevoProyecto.css'

const NuevoProyecto = () => {

    const [proyecto, guardarProyecto] = useState({
        nombre: ''
    })
    const [crearproyecto, guardarCrearProyecto] = useState(false)
    const {nombre} = proyecto

    const onChange = e => {
        guardarProyecto({
            ...proyecto,
            [e.target.name] : e.target.value
        })
    }

    const onSubmit = e => {
        e.preventDefault()
    }
    const crearProyecto = e =>{
        if(e.target.innerHTML === 'Crear Proyecto'){
            guardarCrearProyecto(true)
            e.target.innerHTML = 'Cancelar'
            e.target.classList.add('btnCancelarNuevoProyecto')
            e.target.classList.remove('btnNuevoProyecto')
        }else{
            guardarCrearProyecto(false)
            e.target.innerHTML = 'Crear Proyecto'
            e.target.classList.add('btnNuevoProyecto')
            e.target.classList.remove('btnCancelarNuevoProyecto')
        }
    }
    return (
        <>
            <Container>
                <Button variant='none' className='btnNuevoProyecto mb-5 mt-5' type="submit" block onClick={crearProyecto}>
                    Crear Proyecto
                </Button>
                { crearproyecto ? 
                    <Form
                    onSubmit={onSubmit}
                    >  
                    <Form.Group controlId="nameProyecto">
                        <Form.Control
                            type="text" 
                            placeholder="Nombre de tu proyecto"
                            name='nombre'
                            value={nombre}
                            onChange={onChange}
                        />
                    </Form.Group>

                    <Button variant='none' className='btnNuevoProyecto mb-5' type="submit" block>
                        Agregar Proyecto
                    </Button>
                    </Form> : null
                }
            </Container>
        </>
    );
}
 
export default NuevoProyecto;
