import {useState} from 'react'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import Container from 'react-bootstrap/Container'
import './NuevoProyecto.css'

const NuevoProyecto = () => {

    const [proyecto, guardarProyecto] = useState({
        nombre: ''
    })
    const {nombre} = proyecto
    const onChange = e => {
        guardarProyecto({
            ...proyecto,
            [e.target.name] : e.target.value
        })
    }
    return (
        <>
            <Container>
                <Button variant='none' className='btnNuevoProyecto mb-5 mt-5' type="submit" block>
                    Crear Proyecto
                </Button>
                <Form>  
                    <Form.Group controlId="nameProyecto">
                        <Form.Control
                            type="text" 
                            placeholder="Nombre de tu proyecto"
                            name='nombre'
                            value={nombre}
                            onChange={onChange}
                        />
                    </Form.Group>

                    <Button variant='none' className='btnNuevoProyecto mb-5' type="submit" block>
                        Agregar Proyecto
                    </Button>
                </Form>
            </Container>
        </>
    );
}
 
export default NuevoProyecto;
