import {TAREAS_PROYECTO, AGREGAR_TAREA, VALIDAR_TAREA, ELIMINAR_TAREA, ESTADO_TAREA} from '../../types'


export default (state,action) => {
    switch(action.type){
        case TAREAS_PROYECTO:
            return{
                ...state,
                tareasproyecto : state.tareas.filter(tarea => tarea.proyectoId === action.payload),
                errortarea: false
            }
        case AGREGAR_TAREA:
            return{
                ...state,
                tareas: [action.payload, ...state.tareas],
                errortarea: false
            }
        case VALIDAR_TAREA:
            return{
                ...state,
                errortarea: action.payload
            }
        case ELIMINAR_TAREA:
            return{
                ...state,
                tareas : state.tareas.filter(tarea => tarea.id !== action.payload)
            }
        case ESTADO_TAREA:
            return {
                ...state,
                tareas: state.tareas.filter(tarea => tarea.id === action.payload.id ? action.payload : tarea)
            }
        default:
            return state
    }
}
