import './Tarea.css'

const Tarea = ({tarea}) => {
    return (
        <div className='containerTarea d-flex justify-content-center mt-3'>
            <div className='w-50 tarea'>
                <p>{tarea.nombre}, {tarea.estado ? 'Verdarero' : 'Falso'}</p>
            </div>
        </div>
    );
}
 
export default Tarea;
