import {useState, useContext} from 'react'
import Container from 'react-bootstrap/Container'
import Tarea from './Tarea'
import Row from 'react-bootstrap/Row'
import Button from 'react-bootstrap/Button'
import ProyectoContext from '../../context/Proyectos/proyectoContext'
import './ListadoTareas.css'

const ListadoTareas = () => {
    const proyectosContext = useContext(ProyectoContext)
    const {proyecto} = proyectosContext
    const [mostrar, guardarMostrar] = useState(false)
    if(proyecto){
        const [proyectoSeleccionado] = proyecto
        guardarMostrar(true)
    }
    
    
    const tareasProyecto = [
        {nombre:'Elegir Colores' , estado:true},
        {nombre:'Elegir BD' , estado:false},
        {nombre:'Elegir Plataforma' , estado:false},
        {nombre:'Elegir Componentes' , estado:true}
    ]
    return (
        <Container className='containerListadoTareas mt-4'>
            <Row className='d-flex justify-content-center m-0 p-0'>
                {mostrar
                    ? <h2>wadadw</h2>
                    : <h2>proyectoSeleccionado.nombre</h2>
                }
            </Row>
            <div className='m-0 p-0'>
                    {tareasProyecto.length === 0
                        ? <h3>No hay tareas para mostrar</h3>
                        : tareasProyecto.map(tarea => (
                            <Tarea key={tarea.nombre} tarea={tarea}></Tarea>
                        ))
                    }
            </div>
            <Button variant="light" className='ml-5'>Eliminar Proyecto &times;</Button>
        </Container>
    );
}
 
export default ListadoTareas;
