import {useReducer} from 'react'
import TareaContext from './tareaContext'
import TareaReducer from './tareaReducer'
import {
    TAREAS_PROYECTO,
    AGREGAR_TAREA,
    VALIDAR_TAREA,
    ELIMINAR_TAREA
} from '../../types'

const TareaState = props => {
    const initialState = {
        tareas: [
            {nombre:'Elegir Colores' , estado:true, proyectoId : 1},
            {nombre:'Elegir BD' , estado:false, proyectoId : 1},
            {nombre:'Elegir Plataforma' , estado:false, proyectoId : 2},
            {nombre:'Elegir Componentes' , estado:true, proyectoId : 3}
        ],
        tareasproyecto: null,
        errortarea: false
    }

    const [state,dispatch] = useReducer(TareaReducer,initialState)

    const obtenerTareas = proyectoId =>{
        dispatch({
            type: TAREAS_PROYECTO,
            payload : proyectoId
        })
    }

    const agregarTareas = tareas => {
        dispatch({
            type: AGREGAR_TAREA,
            payload: tareas
        })
    }
    
    const validarTarea = mostrarError =>{
        dispatch({
            type: VALIDAR_TAREA,
            payload: mostrarError
        })
    }
    return(
        <TareaContext.Provider
            value={{
                tareas: state.tareas,
                tareasproyecto: state.tareasproyecto,
                errortarea: state.errortarea,
                obtenerTareas,
                agregarTareas,
                validarTarea
            }}
        >
            {props.children}
        </TareaContext.Provider>
    )
}

export default TareaState
