import {useReducer} from 'react'
import TareaContext from './tareaContext'
import TareaReducer from './tareaReducer'
import {
    TAREAS_PROYECTO,
    AGREGAR_TAREA,
    VALIDAR_TAREA,
    ELIMINAR_TAREA,
    ESTADO_TAREA,
    TAREA_ACTUAL,
    ACTUALIZAR_TAREA,
    LIMPIAR_TAREA
} from '../../types'
import {v4 as uuidv4} from 'uuid';

const TareaState = props => {
    const initialState = {
        tareas: [
            {id : 1, nombre:'Elegir Colores' , estado:true, proyectoId : 1},
            {id : 2, nombre:'Elegir BD' , estado:false, proyectoId : 1},
            {id : 3, nombre:'Elegir Plataforma' , estado:false, proyectoId : 2},
            {id : 4, nombre:'Elegir Componentes' , estado:true, proyectoId : 3}
        ],
        tareasproyecto: null,
        errortarea: false,
        tareaSeleccionada: null
    }

    const [state,dispatch] = useReducer(TareaReducer,initialState)

    const obtenerTareas = proyectoId =>{
        dispatch({
            type: TAREAS_PROYECTO,
            payload : proyectoId
        })
    }

    const agregarTareas = tarea => {
        tarea.id = uuidv4()
        dispatch({
            type: AGREGAR_TAREA,
            payload: tarea
        })
    }
    
    const validarTarea = mostrarError =>{
        dispatch({
            type: VALIDAR_TAREA,
            payload: mostrarError
        })
    }

    const eliminarTarea = tareaId =>{
        dispatch({
            type: ELIMINAR_TAREA,
            payload : tareaId
        })
    }

    const cambiarEstadoTarea = tarea =>{
        dispatch({
            type: ESTADO_TAREA,
            payload: tarea
        })
    }

    const guardarTareaActual = tarea =>{
        dispatch({
            type: TAREA_ACTUAL,
            payload: tarea
        })
    }

    const actualizarTarea = tarea =>{
        dispatch({
            type: ACTUALIZAR_TAREA,
            payload: tarea
        })
    }
    return(
        <TareaContext.Provider
            value={{
                tareas: state.tareas,
                tareasproyecto: state.tareasproyecto,
                errortarea: state.errortarea,
                tareaSeleccionada: state.tareaSeleccionada,
                obtenerTareas,
                agregarTareas,
                validarTarea,
                eliminarTarea,
                cambiarEstadoTarea,
                guardarTareaActual,
                actualizarTarea
            }}
        >
            {props.children}
        </TareaContext.Provider>
    )
}

export default TareaState
