import {
    REGISTRO_EXITOSO,
    REGISTRO_FALLIDO,
    OBTNER_USUARIO,
    LOGIN_EXITOSO,
    LOGIN_FALLIDO,
    CERRAR_SESION
} from '../../types'

const authReducer = (state, action) =>{
    switch(action.type){
        case REGISTRO_EXITOSO:
            localStorage.setItem('token', action.payload.token)
            return{
                ...state,
                autenticado: true,
                mensaje: null
            }
        default:
            return state
    }
}

export default authReducer
