import {useContext} from 'react'
import Container from 'react-bootstrap/Container'
import Tarea from './Tarea'
import Row from 'react-bootstrap/Row'
import Button from 'react-bootstrap/Button'
import ProyectoContext from '../../context/Proyectos/proyectoContext'
import TareaContext from '../../context/tareas/tareaContext'
import { CSSTransition, TransitionGroup} from 'react-transition-group'
import './ListadoTareas.css'

const ListadoTareas = () => {
    const proyectosContext = useContext(ProyectoContext)
    const {proyecto, eliminarProyecto} = proyectosContext
    const tareasContext = useContext(TareaContext)
    const {tareasproyecto} = tareasContext
    if(!proyecto){
        return (
            <Container className='containerListadoTareas mt-4'>
                <Row className='d-flex justify-content-center m-0 p-0'>
                <h2>Selecciona un proyecto</h2>
                </Row>
            </Container>
        );
    }
    
    const [proyectoSeleccionado] = proyecto
    return (
        <Container className='containerListadoTareas mt-4'>
            <Row className='d-flex justify-content-center m-0 p-0'>
            <h2>{proyectoSeleccionado.nombre}</h2>
            </Row>
            <div className='m-0 p-0'>
                    {tareasproyecto.length === 0
                        ? <h3>No hay tareas para mostrar</h3>
                        : tareasproyecto.map(tarea => (
                            <Tarea key={tarea.id} tarea={tarea}></Tarea>
                        ))
                    }
            </div>
            <Button variant="light" className='ml-5' onClick={()=> eliminarProyecto(proyectoSeleccionado.id)}>Eliminar Proyecto &times;</Button>
        </Container>
    );
}
 
export default ListadoTareas;
